import { HttpClient, HttpClientModule } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss']
})
export class EventosComponent {

  public eventos : any ;

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.getEventos();
  }

  public getEventos(): void {
    this.http.get('https://localhost:5001/api/evento').subscribe(
      response =>  this.eventos = response,
      error => console.log(error)
   );
  }
}
<h1>Eventos components</h1>

<div *ngFor="let evento of eventos">
  <p><b>Id:</b> {{evento.eventoId }}</p>
  <p><b>Local:</b> {{evento.local}}</p>
  <p><b>Data evento:</b> {{evento.dataEvento }}</p>
  <p><b>Tema:</b> {{evento.tema}}</p>
  <p><b>Qtd Pessoas:</b> {{evento.qtdPessoas }}</p>
  <p><b>Lote:</b> {{evento.lote}}</p>
  <p><b>Foto:</b> {{evento.imagemURL }}</p>
  <hr>
</div>
